  initialize()
  
  function handleKeyPress(event) {
    // Handle Backspace key first
    if (event.key === 'Backspace') {
      event.preventDefault(); // Prevent browser back navigation
      keyClick('âŒ«');
      return;
    }
    // Handle Enter key
    else if (event.key === 'Enter') {
      event.preventDefault(); // Prevent form submission or page reload
      keyClick('enter');
      return;
    }
    // Handle letter keys
    else if (event.key.length === 1 && event.key.match(/[a-zA-Z]/)) {
      event.preventDefault(); // Prevent default behavior
      keyClick(event.key.toLowerCase());
      return;
    }
    // Ignore all other keys
  }
  
  function keyClick(key) {
    switch (key) {
      case 'âŒ«':
        backspace();
        break;
      case 'enter':
        enter(); // This will now be async
        break;
      default:
        if (currentGuess.length < SecretWord.length
          && guesses.length < NumberOfGuesses) {
          currentGuess.push({ key: key, result: '' });
          updateCurrentGuess();
        }
    }
  }
  
  function backspace() {
    if (currentGuess.length > 0) {
      currentGuess.pop();
    }
    updateCurrentGuess();
  }
  
  async function isValidWord(word) {
    // Convert to lowercase for comparison
    const lowerWord = word.toLowerCase();
    
    // First check if word is in the answer key (words) - these are always valid
    if (words.includes(word.toUpperCase())) {
      return true;
    }
    
    // Check if word is in our local word list
    if (word_list.includes(lowerWord)) {
      return true;
    }
    
    // For words not in our local list, use dictionary API
    try {
      const response = await fetch(`https://api.dictionaryapi.dev/api/v2/entries/en/${lowerWord}`);
      if (response.ok) {
        const data = await response.json();
        // If API returns data, the word exists in the dictionary
        return data && data.length > 0;
      }
      return false;
    } catch (error) {
      console.log('Dictionary API error:', error);
      // If API fails, fall back to local word list only
      return false;
    }
  }

  async function enter() {
    if (currentGuess.length === SecretWord.length) {
        let guessedWord = currentGuess.map(c => c.key).join('').toUpperCase();
        let secret = SecretWord.toUpperCase();

        // Validate the guessed word
        const isValid = await isValidWord(guessedWord);
        if (!isValid) {
            showInvalidWordAlert("Not in word list");
            currentGuess = []; // Clear the current guess
            updateCurrentGuess(); // Update the display
            return; // Don't process the guess if it's invalid
        }

        let secretLetterCount = {};
        for (let letter of secret) {
            if (secretLetterCount[letter]) {
                secretLetterCount[letter]++;
            } else {
                secretLetterCount[letter] = 1;
            }
        }

        currentGuess.forEach((keyGuess, index) => {
            if (secret[index] === keyGuess.key.toUpperCase()) {
                keyGuess.result = Correct;
                secretLetterCount[keyGuess.key.toUpperCase()]--;
                keys[keyGuess.key] = 'correct'; // Update keys object with 'correct'
            }
        });

        currentGuess.forEach((keyGuess, index) => {
            if (!keyGuess.result && secret.includes(keyGuess.key.toUpperCase()) && secretLetterCount[keyGuess.key.toUpperCase()] > 0) {
                keyGuess.result = Found;
                secretLetterCount[keyGuess.key.toUpperCase()]--;
                keys[keyGuess.key] = 'found'; // Update keys object with 'found'
            } else if (!keyGuess.result) {
                keyGuess.result = Wrong;
                keys[keyGuess.key] = 'wrong'; // Update keys object with 'wrong'
            }
        });

        updateCurrentGuess(true);
        guesses.push([...currentGuess]);
        currentGuess = [];

        if (guessedWord === secret) {
            showAlert("Rah Rah Right answer!"); // Show the popup
            setTimeout(showStatsPopup, 2000); // Show the stats popup after 2 seconds
            updateStatsOnWin(); // Update stats as a win
        }
     else if (guesses.length >= NumberOfGuesses) {
        showGameOverPopup(); // Exceeded max tries
        updateStatsOnLoss(); // Update stats as a loss
      } else {
        currentGuess = []; // Prepare for the next guess
      }
        updateKeyboard(); // Make sure to call this to update the colors
    }
}


function showAlert(message) {
    document.getElementById('popupMessage').innerText = message;
    document.getElementById('customPopup').classList.remove('hidden');

    setTimeout(() => {
        closePopup();
        showStatsPopup();
    }, 2000);
}

function showInvalidWordAlert(message) {
    document.getElementById('popupMessage').innerText = message;
    document.getElementById('customPopup').classList.remove('hidden');

    setTimeout(() => {
        closePopup();
        // Don't show stats popup for invalid words
    }, 2000);
}

function closePopup() {
    // Hide the popup
    document.getElementById('customPopup').classList.add('hidden');
}

function updateKeyboard() {
    for (const key in keys) {
        let keyElement = document.getElementById(key);
        if (keyElement) {
            keyElement.className = 'key'; // Reset the class
            if (['correct', 'found', 'wrong'].includes(keys[key])) {
                keyElement.classList.add(keys[key]); // Add the new class
            }
        }
    }
}

  function updateCurrentGuess(guessed = false) {
    let index = guesses.length;
    for (let i = 0; i < SecretWord.length; i++) {
      let guessGrid = document.getElementById(`${index}${i}`);
      if (currentGuess[i]) {
        guessGrid.innerHTML = currentGuess[i].key;
      } else {
        guessGrid.innerHTML = '';
      }
      if (guessed) {
        guessGrid.classList.add(currentGuess[i].result);
      }
    }
    if (guessed) {
      updateKeyboard();
    }
  }

